mapper.update:
UPDATE replys
reply = #{reply} , reg_date = SYSDATE
WHERE reply_no = #{replyNo}

mapper.delete:
DELETE
FROM replys
WHERE
reply_no IN (SELECT reply_no FROM replys
	where 
	reply_no =#{replyNo}
				
	
		OR par_reply_no=#{parentReplyNo}
	
		
)

mapper.select:
  	SELECT COUNT(*)
  	FROM(	SELECT reply_no , user_no , content_no , reply , reg_date, par_reply_no
				   FROM replys		   
				   WHERE content_no = #{value}
				   and par_reply_no = 0
				   ) countTable

  	SELECT COUNT(*)
  	FROM( SELECT reply_no , user_no , content_no , reply , reg_date, par_reply_no
				   FROM replys		   
				   WHERE par_reply_no = #{value}
				   ) countTable
